    return updatedAuthor;
  },
  addAuthorToPublisher: async (_, { id, publisher }) => {
    const author = await Author.findById(id);

    if (author) {
      const newWorksAt = new WorksAt({ publisher, author: id });
      await newWorksAt.save();
    }

    return author;
  },
};

export default authorMutations;

